package com.bayviewglen.daythree;

public class ExampleThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// these will run forever
		
		int x = 8;
		while (x>7) {
			x++;
			System.out.println("Eight ate 8");
		}
		
		for (int y = 8; y < 7; y++) {
			System.out.println("Eight ate 8");
		}
	}

}
